import React, { useState } from 'react'
import MyButton from '../UI/Button/MyButton';
import MyInput2 from '../UI/Input/MyInput2';

const NewNam = ({ create }) => {
    const [dela, setDela] = useState({ nam: '', title: '', main: '' });

    const addNewDela = (e) => {
        e.preventDefault();
        if (dela.name === '' || dela.title === '' || dela.main === '') {
            alert("Заполните все необходимы поля");
        }
        else {
            const newDela = {
                ...dela, id: Date.now()
            }
            create(newDela)
            setDela({ name: '', title: '', main: '' })
        }
    }

    return (
        <div>
            <div style={{ display: "flex", flexDirection: "column", alignItems: "end", width: '274px' }}>
                <MyInput2
                    placeholder="Событие"
                    title={"Собитие"}
                    value={dela.nam}
                    onChange={e => {
                        setDela({ ...dela, nam: e.target.value })
                    }}
                />
            </div>
            <MyButton
                onClick={() => {
                }
                }
            >Добавить</MyButton>
        </div >
    )
}

export default NewNam
