  0x0e968, 0x0d520, 0x0daa0, 0x16aa6, 0x056d0, 0x04ae0, 0x0a9d4, 0x0a4d0, 0x0d150, 0x0f252, // 2090-2099
  0x0d520, // 2100
];
import { lunarInfo } from './constants';

class Solar2lunar {
  constructor(date) {
    this.y = date.getFullYear();
    this.m = date.getMonth();
    this.d = date.getDate();
  }

  /**
   * 返回农历y年闰月是哪个月；若y年没有闰月 则返回0
   * @param y
   * @returns {number}
   */
  leapMonth(y) {
    return (lunarInfo[y - 1900] & 0xf);
  }

  leapDays(y) {
    if (this.leapMonth(y)) {
      return ((lunarInfo[y - 1900] & 0x10000) ? 30 : 29);
    }
    return (0);
  }

  lYearDays(y) {
    let i;
    let sum = 348;
    for (i = 0x8000; i > 0x8; i >>= 1) {
      sum += (lunarInfo[y - 1900] & i) ? 1 : 0;
    }
    return (sum + this.leapDays(y));
  }

  monthDays(y, m) {
    return ((lunarInfo[y - 1900] & (0x10000 >> m)) ? 30 : 29);
  }

  convert() {
    const { y, m, d } = this;
    if (y < 1900 || y > 2100 || (y === 1900 && !m && d < 31)) return {};

    let i;
    let temp = 0;
    let offset = (Date.UTC(y, m, d) - Date.UTC(1900, 0, 31)) / 86400000;
    for (i = 1900; i < 2101 && offset > 0; i++) {
      temp = this.lYearDays(i);
      offset -= temp;
    }
    if (offset < 0) {
      offset += temp;
      i -= 1;
    }

    const year = i; // 农历年
    const leap = this.leapMonth(i); // 闰哪个月

    let isLeap = false;
    for (i = 1; i < 13 && offset > 0; i++) {
      if (leap > 0 && i === (leap + 1) && !isLeap) { // 闰月
        i -= 1;
        isLeap = true;
        temp = this.leapDays(year); // 计算农历闰月天数
      } else {
        temp = this.monthDays(year, i);// 计算农历普通月天数
      }
      isLeap = !(isLeap && i === (leap + 1)); // 解除闰月
      offset -= temp;
    }

    if (!offset && leap > 0 && i === leap + 1) {
      isLeap = !isLeap;
      if (isLeap) i -= 1;
    }
    if (offset < 0) {
      offset += temp;
      i -= 1;
    }
    const month = i; // 农历月
    const day = offset + 1; // 农历日

    return { year, leap, month, day };
  }
}

export default Solar2lunar;
import { animals } from './constants';
import Solar2lunar from './solar2lunar';

export const getChinaStandard = (date, streamline) => {
  let month = date.getMonth() + 1;
  let day = date.getDate();
  month = `${month < 10 ? '0' : ''}${month}`;
  day = `${day < 10 ? '0' : ''}${day}`;
  const arr = streamline ? [date.getFullYear(), month] : [date.getFullYear(), month, day];
  return arr.join('-');
};

export const getMonthDays = (date) => {
  const year = date.getFullYear();
  const month = date.getMonth();
  const leapYear = !(year % 4) && ((year % 100) || !(year % 400));
  const days = [31, leapYear ? 29 : 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];
  return days[month];
};

export const getDate = (dateStr) => {
  const date = new Date();
  const [year, month] = dateStr.split('-');
  date.setFullYear(+year);
  date.setMonth(month - 1, 1);
  return date;
};

export const eraseTime = (timeStamp) => {
  const date = new Date(timeStamp);
  date.setHours(0);
  date.setMinutes(0);
  date.setSeconds(0);
  date.setMilliseconds(0);
  return date.getTime();
};

export const calcMonth = date => date.getFullYear() * 12 + date.getMonth() + 1;

export const getAnimal = date => animals[date.getFullYear() % 12];

export const lunar = date => new Solar2lunar(date).convert();
import * as utils from './utils';

class Day {
  constructor(date, extension = {}) {
    const toDay = utils.eraseTime(new Date().getTime());
    const timestamp = utils.eraseTime(date.getTime());
    const dateText = utils.getChinaStandard(date);
    const assignment = {
      year: date.getFullYear(),
      month: date.getMonth(),
      date: date.getDate(),
      day: date.getDay(),
      past: toDay > timestamp,
      today: toDay === utils.eraseTime(timestamp),
      animal: utils.getAnimal(date),
      lunar: utils.lunar(date),
      dateText,
      timestamp,
      ...extension,
    };
    Object.keys(assignment).forEach(key => this.set(key, assignment[key]));
  }

  set(key, val) {
    this[key] = val;
  }
}

export default Day;
import Day from './day';
import * as utils from './utils';

class Kalendar {
  constructor({ start, end, unifiedMount = {}, mount = {}, weekStart = 0 } = {}) {
    this.startTime = start;
    this.endTime = end;
    this.unifiedMount = unifiedMount;
    this.mount = mount;
    this.weekStart = weekStart;
    return this._create();
  }

  get startDate() {
    const date = this.startTime ? utils.getDate(this.startTime) : new Date();
    date.setDate(1);
    return date;
  }

  get endDate() {
    if (this.endTime) return utils.getDate(this.endTime);
    const date = this.startDate;
    date.setMonth(date.getMonth() + 3);
    return date;
  }

  _create() {
    const { startDate, endDate } = this;
    const table = {};
    const count = utils.calcMonth(endDate) - utils.calcMonth(startDate);
    const startTime = utils.eraseTime(startDate.getTime());
    if (count < 0) return null;
    let idx = 0;
    do {
      const date = new Date(startTime);
      date.setMonth(date.getMonth() + idx);
      table[utils.getChinaStandard(date, true)] = Kalendar.monthly({ date, ...this });
      idx += 1;
    } while (count > idx);
    return table;
  }

  static monthly({ date, mount = {}, unifiedMount = {}, weekStart = 0, continuous }) {
    date.setDate(1);
    const monthTable = [];
    const days = utils.getMonthDays(date);
    const day = date.getDay();
    let skip = 0;
    if (day !== weekStart) skip = (day || 7) - weekStart;
    for (let i = 0; i < days + skip; i += 7) {
      const week = [];
      let num = 7;
      if (!i && skip) {
        for (let k = 0; k < skip; k++) {
          if (continuous) {
            const agoneDate = new Date(date.valueOf());
            agoneDate.setDate(agoneDate.getDate() - skip + k);
            const previousDateText = utils.getChinaStandard(agoneDate);
            week.push(new Day(agoneDate, Object.assign({}, unifiedMount, mount[previousDateText])));
          } else {
            week.push(null);
          }
        }
        num -= skip;
      }
      for (let j = 0; j < num; j++) {
        const dateText = utils.getChinaStandard(date);
        week.push(new Day(date, Object.assign({}, unifiedMount, mount[dateText])));
        if (date.getDate() >= days) break;
        date.setDate(date.getDate() + 1);
      }
      const futureDate = new Date(date.valueOf());
      while (week.length < num) {
        if (continuous) {
          futureDate.setDate(futureDate.getDate() + 1);
          const dateText = utils.getChinaStandard(futureDate);
          week.push(new Day(futureDate, Object.assign({}, unifiedMount, mount[dateText])));
        } else {
          week.push(null);
        }
      }
      monthTable.push(week);
    }
    return monthTable;
  }
}

export default Kalendar;
