          setContext('daysViewLayout', res.normalPositions);
          setContext('layoutUpdateSequence', store.layoutUpdateSequence + 1);

          const days: any = renderOneDay(
            store.calendarDays,
            res.normalPositions,
            store.layoutUpdateSequence + 1
          );
          setCalendarContent(days);
        });
      }
    }
  }, [calendarDays[0], selectedView]);

  useLayoutEffect(() => {
    if (wasInit) {
      if (!hasExternalLayout) {
        KalendLayout({
          events,
          width,
          height,
          calendarDays,
          config: store.config,
          isMobile,
          selectedView,
        }).then((res: any) => {
          setContext('headerLayout', res.headerPositions);
          setContext('headerEventRowsCount', res.headerOffsetTop);
          setContext('daysViewLayout', res.normalPositions);
          setContext('layoutUpdateSequence', store.layoutUpdateSequence + 1);

          const days: any = renderOneDay(
            store.calendarDays,
            res.normalPositions,
            store.layoutUpdateSequence + 1
          );
          setCalendarContent(days);
        });
      }
    }
  }, [width]);

  useLayoutEffect(() => {
    if (!hasExternalLayout) {
      KalendLayout({
        events,
        width,
        height,
        calendarDays,
        config: store.config,
        isMobile,
        selectedView,
      }).then((res: any) => {
        setContext('headerLayout', res.headerPositions);
        setContext('headerEventRowsCount', res.headerOffsetTop);
        setContext('daysViewLayout', res.normalPositions);
        setContext('layoutUpdateSequence', store.layoutUpdateSequence + 1);

        const days: any = renderOneDay(
          store.calendarDays,
          res.normalPositions,
          store.layoutUpdateSequence + 1
        );

        setCalendarContent(days);
      });
    }
  }, [JSON.stringify(events)]);

  useLayoutEffect(() => {
    if (!hasExternalLayout) {
      KalendLayout({
        events,
        width,
        height,
        calendarDays,
        config: store.config,
        isMobile,
        selectedView,
      }).then((res: any) => {
        setContext('headerLayout', res.headerPositions);
        setContext('headerEventRowsCount', res.headerOffsetTop);
        setContext('daysViewLayout', res.normalPositions);
        setContext('layoutUpdateSequence', store.layoutUpdateSequence + 1);

        const days: any = renderOneDay(
          store.calendarDays,
          res.normalPositions,
          store.layoutUpdateSequence + 1
        );

        setCalendarContent(days);
      });
    }
  }, [config.hourHeight]);

  useLayoutEffect(() => {
    if (!hasExternalLayout) {
      KalendLayout({
        events,
        width,
        height,
        calendarDays,
        config: store.config,
        isMobile,
        selectedView,
      }).then((res: any) => {
        setContext('headerLayout', res.headerPositions);
        setContext('headerEventRowsCount', res.headerOffsetTop);
        setContext('daysViewLayout', res.normalPositions);
        setContext('layoutUpdateSequence', store.layoutUpdateSequence + 1);

        const days: any = renderOneDay(
          store.calendarDays,
          res.normalPositions,
          store.layoutUpdateSequence + 1
        );
        setCalendarContent(days);
      });
    }
    setWasInit(true);
  }, []);

  useLayoutEffect(() => {
    if (
      hasExternalLayout &&
      getSelectedViewType(props.eventLayouts.selectedView) ===
        CALENDAR_VIEW.WEEK
    ) {
      setContext('headerLayout', props.eventLayouts.headerPositions);
      setContext('headerEventRowsCount', props.eventLayouts.headerOffsetTop);
      setContext('daysViewLayout', props.eventLayouts.normalPositions);
      setContext('layoutUpdateSequence', store.layoutUpdateSequence + 1);

      const days: any = renderOneDay(
        store.calendarDays,
        props.eventLayouts.normalPositions,
        store.layoutUpdateSequence + 1
      );
      setCalendarContent(days);
    }
  }, [props.eventLayouts, JSON.stringify(props.eventLayouts)]);

  return (
    <div
      style={style}
      className={'Kalend__CalendarBody'}
      id={`Kalend__timetable`}
      // onScroll={handleScroll}
    >
      <CalendarBodyHours />
      <DaysViewVerticalLines />
      {calendarContent}
    </div>
  );
};

