import { MonthViewButtonMoreProps } from './MonthViewButtonMore.props';
import { formatDateTimeToString, parseCssDark } from '../../../utils/common';
import { useContext } from 'react';
import Dropdown from '../../dropdown/Dropdown';
import EventButton from '../../eventButton/EventButton';

const MonthViewButtonMore = (props: MonthViewButtonMoreProps) => {
  const [store, dispatch] = useContext(Context);
  const setContext = (type: string, payload: any) => {
    dispatch({ type, payload });
  };

  const { width, monthOverflowEvents } = store;

  const { calendarDays } = props;

  const colWidth: number = width / 7;

  const handleClick = (day: DateTime, events: CalendarEvent[]) => {
    setContext('showMoreEvents', { day, events });
  };

  const renderShowMoreButtons = () => {
    if (!monthOverflowEvents || !calendarDays || calendarDays.length === 0) {
      return [];
    }

    return calendarDays.map((calendarDay: DateTime) => {
      const dateKey: string = formatDateTimeToString(calendarDay);
      const events: CalendarEvent[] | undefined = monthOverflowEvents[dateKey];

      if (events) {
        return (
          <Dropdown
            onClick={() => handleClick(calendarDay, events)}
            width={colWidth}
            key={calendarDay.toString()}
            day={calendarDay}
          >
            <>
              <h6
                className={parseCssDark(
                  'Kalend__MonthView_more_title',
                  store.isDark
                )}
              >
                {calendarDay.toFormat('dd. MMM')}
              </h6>
              {/*// @ts-ignore*/}
              {events?.map((event: any) => {
                return (
                  <EventButton
                    key={`${event.id}${
                      event.internalID ? event.internalID : ''
                    }`}
                    item={{ event }}
                    type={EVENT_TYPE.SHOW_MORE_MONTH}
                  />
                );
              })}
            </>
          </Dropdown>
        );
      } else {
        return (
          <div
            key={calendarDay.toString()}
            style={{ width: colWidth, visibility: 'hidden' }}
          />
        );
      }
    });
  };

  const showMoreButtons: any = renderShowMoreButtons();

  return (
    <div
      style={{
        width: '100%',
        display: 'flex',
        flexDirection: 'row',
        position: 'absolute',
        bottom: 1,
        left: 0,
      }}
    >
      {showMoreButtons}
      {/*{showMoreEvents ? <ShowMoreModal /> : null}*/}
    </div>
  );
};

export default MonthViewButtonMore;
import { Context } from '../../../context/store';
import { EVENT_TYPE } from '../../../common/enums';
import { MonthWeekRowProps } from './MonthWeekRow.props';
import { parseCssDark } from '../../../utils/common';
import { useContext } from 'react';
import CalendarHeaderDates from '../../calendarHeader/components/calendarHeaderDates/CalendarHeaderDates';
import EventButton from '../../eventButton/EventButton';
import MonthViewButtonMore from '../monthViewButtonMore/MonthViewButtonMore';

const MonthWeekRow = (props: MonthWeekRowProps) => {
  const { days, index, itemRows } = props;

  const [store] = useContext(Context);
  const { monthLayout } = store;

  const renderEvents = (data: any, i: number) => {
    if (!data || !data?.[i]) {
      return [];
    }
    return itemRows.map((item: any) => {
      return (
        <EventButton
          key={`${item.event.id}${
            item.event.internalID ? item.event.internalID : ''
          }`}
          item={{ ...item }}
          meta={item.meta}
          type={EVENT_TYPE.MONTH}
          index={i}
        />
      );
    });
  };

  const events: any = renderEvents(monthLayout, index);

  // const style: { maxHeight: number } = { maxHeight: height / 6 - 30 };

  return (
    <div
      className={`${parseCssDark(
        'Kalend__MonthWeekRow__container',
        store.isDark
      )} ${index > 4 ? '--no-border' : ''}`}
    >
      <div className={'Kalend__MonthWeekRow__day'}>
        <CalendarHeaderDates
          calendarDays={days}
          daysNum={7}
          setViewChanged={props.setViewChanged}
        />
      </div>
      <div className={'Kalend__MonthWeekRow__container-events'}>{events}</div>
      <MonthViewButtonMore calendarDays={days} />
    </div>
  );
};

export default MonthWeekRow;
import { Context } from '../../context/store';
import { DateTime } from 'luxon';

import { CALENDAR_VIEW } from '../../common/enums';
import { MonthViewProps } from './MonthView.props';
import { getMonthRows } from './monthWeekRow/MonthWeekRow.utils';
import { getSelectedViewType } from '../../utils/common';
import { useContext, useEffect, useState } from 'react';
import DaysViewVerticalLines from '../daysViewTable/daysViewVerticalLines/DaysViewVerticalLines';
import KalendLayout from 'kalend-layout';
import MonthWeekRow from './monthWeekRow/MonthWeekRow';

const renderOneRow = (
  days: DateTime[],
  eventRows: any,
  sequence: number,
  setViewChanged: any
) => {
  const rows: DateTime[][] = getMonthRows(days);

  return rows.map((row: DateTime[], index: number) => {
    return (
      <MonthWeekRow
        key={row[0].toString() + sequence}
        days={row}
        index={index}
        itemRows={eventRows ? eventRows[index] : []}
        sequence={sequence}
        setViewChanged={setViewChanged}
      />
    );
  });
};

const MonthView = (props: MonthViewProps) => {
  const [wasInit, setWasInit] = useState(false);
  const [calendarContent, setCalendarContent] = useState(null);

  const { events } = props;
  const [store, dispatch] = useContext(Context);
  const setContext = (type: string, payload: any) => {
    dispatch({ type, payload });
  };

  const { rawWidth, width, calendarDays, height, showWeekNumbers } = store;

  const style: any = {
    width: showWeekNumbers ? width : rawWidth,
    height: '100%',
  };

  const hasExternalLayout = props.eventLayouts !== undefined;

  useEffect(() => {
    if (height !== 0) {
      if (!hasExternalLayout) {
        KalendLayout({
          events,
          width: showWeekNumbers ? width : rawWidth,
          height,
          calendarDays,
          config: store.config,
          selectedView: CALENDAR_VIEW.MONTH,
        }).then((res: any) => {
          setWasInit(true);
          setContext('monthLayout', res.positions);
          setContext('monthOverflowEvents', res.overflowingEvents);
          setContext('layoutUpdateSequence', store.layoutUpdateSequence + 1);

          const content: any = renderOneRow(
            calendarDays,
            res.positions,
            store.layoutUpdateSequence,
            props.setViewChanged
          );
          setCalendarContent(content);
        });
      }
    }
  }, [height, rawWidth]);

  useEffect(() => {
    if (wasInit && height !== 0) {
      if (!hasExternalLayout) {
        KalendLayout({
          events,
          width: showWeekNumbers ? width : rawWidth,
          height,
          calendarDays,
          config: store.config,
          selectedView: CALENDAR_VIEW.MONTH,
        }).then((res: any) => {
          setContext('monthLayout', res.positions);
          setContext('monthOverflowEvents', res.overflowingEvents);
          setContext('layoutUpdateSequence', store.layoutUpdateSequence + 1);
          const content: any = renderOneRow(
            calendarDays,
            res.positions,
            store.layoutUpdateSequence,
            props.setViewChanged
          );
          setCalendarContent(content);
        });
      }
    }
  }, [calendarDays[0], JSON.stringify(events)]);

  useEffect(() => {
    if (
      hasExternalLayout &&
      getSelectedViewType(props.eventLayouts.selectedView) ===
        CALENDAR_VIEW.MONTH
    ) {
      setContext('monthLayout', props.eventLayouts.positions);
      setContext('monthOverflowEvents', props.eventLayouts.overflowingEvents);
      setContext('layoutUpdateSequence', store.layoutUpdateSequence + 1);

      const content: any = renderOneRow(
        calendarDays,
        props.eventLayouts.positions,
        store.layoutUpdateSequence,
        props.setViewChanged
      );
      setCalendarContent(content);
    }
  }, [JSON.stringify(props.eventLayouts)]);

  return (
    <div className={'Kalend__MonthView__container'} style={style}>
      <DaysViewVerticalLines />
      {/*{rows}*/}
      {calendarContent}
    </div>
  );
};

export default MonthView;
import { Context } from '../../context/store';
import { DateTime } from 'luxon';
import { useContext } from 'react';

const renderCols = (calendarDays: DateTime[], translations: any) => {
  return calendarDays.map((item, index) => {
    if (index % 7 === 0 && index < 7 * 5) {
      const weekNum = item.weekNumber;
      return (
        <div className={'Kalend__WeekNumbersCol__container'}>
          <p className={'Kalend__WeekNumbersCol__text'}>
            {translations['weekShort']}
            {weekNum}
          </p>
        </div>
      );
    } else if (index % 7 === 0 && index >= 7 * 5) {
      const weekNum = item.weekNumber;
      return (
        <div className={'Kalend__WeekNumbersCol__container--no-border'}>
          <p className={'Kalend__WeekNumbersCol__text'}>
            {translations['weekShort']}
            {weekNum}
          </p>
        </div>
      );
    }
  });
};

const WeekNumbersCol = () => {
  const [store] = useContext(Context);

  const cols = renderCols(store.calendarDays, store.translations);
  return <div className={'Kalend__WeekNumbersCol__wrapper'}>{cols}</div>;
};

export default WeekNumbersCol;
import { CALENDAR_VIEW } from './common/enums';
import { CalendarProps } from './Calendar.props';
import { Context, Store } from './context/store';
import { DateTime } from 'luxon';
import { getCalendarDays, getRange } from './utils/calendarDays';
import { parseCssDark } from './utils/common';
import { useContext, useEffect, useLayoutEffect, useState } from 'react';
import AgendaView from './components/agendaView/AgendaView';
import CalendarDesktopNavigation from './components/CalendarDesktopNavigation/CalendarDesktopNavigation';
import CalendarHeader from './components/calendarHeader/CalendarHeader';
import CalendarTableLayoutLayer from './layers/CalendarTableLayoutLayer';
import DaysViewTable from './components/daysViewTable/DaysViewTable';
import MonthView from './components/monthView/MonthView';
import WeekNumbersCol from './components/weekNumbersCol/WeekNumbersCol';

const Calendar = (props: CalendarProps) => {
  const [store, dispatch] = useContext(Context);
  const setContext = (type: string, payload: any) => {
    dispatch({ type, payload });
  };

  const { selectedDate, calendarDays, selectedView, callbacks, config, width } =
    store as Store;

  const [prevView, setPrevView] = useState('');
  const [viewChanged, setViewChanged] = useState<any>(null);

  useEffect(() => {
    const initialDate = props.initialDate
      ? DateTime.fromISO(props.initialDate)
      : DateTime.now();

    setContext('selectedDate', initialDate);

    if (selectedView) {
      const calendarDaysInitial: DateTime[] = getCalendarDays(
        selectedView,
        initialDate,
        config.weekDayStart
      );

      setContext('calendarDays', calendarDaysInitial);
    }
  }, []);

  useEffect(() => {
    const viewChangedValue = props.selectedView || viewChanged;
    // if (props.selectedView && props.selectedView === selectedView) {
    //   return;
    // }
    if (prevView === viewChangedValue) {
      return;
    }
    // prevent infinit loop
    if (!selectedView && callbacks.onSelectView) {
      callbacks.onSelectView(viewChangedValue);
      return;
    }
    if (!viewChangedValue) {
      return;
    }

    setContext('calendarDays', calendarDays[0]);
    setContext('selectedView', viewChangedValue);

    // use either passed value or internal state
    const setSelectedDate = (date: DateTime) => {
      setContext('selectedDate', date);
    };

    const calendarDaysNew: DateTime[] = getCalendarDays(
      viewChangedValue,
      selectedDate || props.initialDate || DateTime.now(),
      config.weekDayStart,
      setSelectedDate
    );

    setContext('calendarDays', calendarDaysNew);

    setContext('layoutUpdateSequence', store.layoutUpdateSequence + 1);
    setPrevView(viewChangedValue);
    setViewChanged(null);
  }, [viewChanged, props.selectedView]);

  useEffect(() => {
    const selectedViewValue = props.selectedView || selectedView;

    if (prevView === selectedViewValue) {
      return;
    }
    if (selectedViewValue && selectedViewValue !== prevView) {
      setContext('calendarDays', calendarDays[0]);
      setContext('selectedView', selectedViewValue);
      setPrevView(selectedViewValue);

      const setSelectedDate = (date: DateTime) =>
        setContext('selectedDate', date);

      const calendarDaysNew: DateTime[] = getCalendarDays(
        selectedViewValue,
        selectedDate || props.initialDate || DateTime.now(),
        config.weekDayStart,
        setSelectedDate
      );

      setContext('calendarDays', calendarDaysNew);
    }
  }, [selectedView]);

  useLayoutEffect(() => {
    setContext('events', props.events);
  }, [JSON.stringify(props.events)]);

  useLayoutEffect(() => {
    if (
      callbacks.onPageChange &&
      calendarDays &&
      calendarDays[0] &&
      calendarDays.length > 0
    ) {
      callbacks.onPageChange({
        ...getRange(calendarDays),
        direction: store.direction,
      });
    }
  }, [
    selectedView,
    calendarDays?.[0],
    calendarDays?.[calendarDays?.length - 1],
  ]);

  return selectedView && calendarDays?.length > 0 && selectedDate && width ? (
    <>
      <CalendarDesktopNavigation
        setViewChanged={setViewChanged}
        kalendRef={props.kalendRef}
      />
      {selectedView !== CALENDAR_VIEW.AGENDA &&
      selectedView !== CALENDAR_VIEW.MONTH ? (
        <CalendarHeader setViewChanged={setViewChanged} />
      ) : null}
      {selectedView === CALENDAR_VIEW.MONTH ? (
        <>
          <CalendarHeader />
          <div
            style={{
              display: 'flex',
              flexDirection: 'row',
              width: '100%',
              height: '100%',
            }}
          >
            {store.showWeekNumbers ? <WeekNumbersCol /> : null}
            <div className={'Kalend__Calendar__table'}>
              <div
                className={parseCssDark(
                  'Kalend__Calendar__table-surface',
                  store.isDark
                )}
              >
                <CalendarTableLayoutLayer>
                  <MonthView
                    events={props.events ? props.events : []}
                    eventLayouts={props.eventLayouts}
                    setViewChanged={setViewChanged}
                  />
                </CalendarTableLayoutLayer>
              </div>
            </div>
          </div>
        </>
      ) : (
        <div className={'Kalend__Calendar__table'}>
          <div
            className={parseCssDark(
              'Kalend__Calendar__table-surface',
              store.isDark
            )}
          >
            <CalendarTableLayoutLayer>
              {selectedView === CALENDAR_VIEW.DAY ||
              selectedView === CALENDAR_VIEW.THREE_DAYS ||
              selectedView === CALENDAR_VIEW.WEEK ? (
                <DaysViewTable
                  events={props.events ? props.events : []}
                  eventLayouts={props.eventLayouts}
                />
              ) : null}

              {selectedView === CALENDAR_VIEW.AGENDA ? (
                <AgendaView
                  events={props.events ? props.events : []}
                  eventLayouts={props.eventLayouts}
                />
              ) : null}
            </CalendarTableLayoutLayer>
          </div>
        </div>
      )}
    </>
  ) : null;
};

export default Calendar;
import { Context } from '../context/store';
import { getTableOffset } from '../utils/common';
import { useContext, useEffect } from 'react';

const DimensionsLayoutLayer = (props: { children: any }) => {
  const [store, dispatch] = useContext(Context);
  const setContext = (type: string, payload: any) => {
    dispatch({ type, payload });
  };
  const { selectedView, headerEventRowsCount, showWeekNumbers } = store;

  useEffect(() => {
    const el = document?.querySelector('.Kalend__Calendar__root');

    if (!el) {
      return;
    }
    const resizeObserver = new ResizeObserver((entries) => {
      const entryRect = entries[0].contentRect;

      const width = entryRect.width;

      if (width < 750) {
        setContext('isMobile', true);
      } else {
        setContext('isMobile', false);
      }

      setContext('rawWidth', entryRect.width);
      setContext(
        'width',
        entryRect.width - getTableOffset(selectedView, showWeekNumbers)
      );
    });

    resizeObserver.observe(el);
  }, [document?.querySelector('.Kalend__Calendar__root'), selectedView]);

  useEffect(() => {
    const el = document?.querySelector('.Kalend__Calendar__root');

    if (!el) {
      return;
    }
    const resizeObserver = new ResizeObserver((entries) => {
      const entryRect = entries[0].contentRect;

      const width = entryRect.width;

      if (width < 750) {
        setContext('isMobile', true);
      } else {
        setContext('isMobile', false);
      }

      setContext('rawWidth', entryRect.width);
      setContext(
        'width',
        entryRect.width - getTableOffset(selectedView, showWeekNumbers)
      );
    });

    resizeObserver.observe(el);
  }, []);

  useEffect(() => {
    const el = document?.querySelector('.Kalend__Calendar__table');

    if (!el) {
      return;
    }
    const resizeObserver = new ResizeObserver((entries) => {
      const entryRect = entries[0].contentRect;

      setContext('height', entryRect.height);
    });

    resizeObserver.observe(el);
  }, [
    document?.querySelector('.Kalend__Calendar__table'),
    selectedView,
    headerEventRowsCount,
  ]);

  useEffect(() => {
    const el = document?.querySelector('.Kalend__Calendar__table');

    if (!el) {
      return;
    }
    const resizeObserver = new ResizeObserver((entries) => {
      const entryRect = entries[0].contentRect;

      setContext('height', entryRect.height);
    });

    resizeObserver.observe(el);
  }, []);

  return props.children;
};

