import { Context } from '../context/store';
import { useContext, useEffect } from 'react';
import de from '../locales/de.json';
import en from '../locales/en.json';
import es from '../locales/es.json';
import fr from '../locales/fr.json';
import ptBR from '../locales/ptBR.json';
import ru from '../locales/ru.json';
import zh from '../locales/zh.json';

const getKnownLanguage = (language: string) => {
  switch (language) {
    case 'en':
      return en;
    case 'de':
      return de;
    case 'es':
      return es;
    case 'fr':
      return fr;
    case 'ptBR':
      return ptBR;
    case 'ru':
      return ru;
    case 'zh':
      return zh;
    default:
      return 'en';
  }
};

const LanguageLayer = (props: {
  children: any;
  language?: string;
  customLanguage?: any;
}) => {
  const { language, customLanguage } = props;

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const [store, dispatch] = useContext(Context);
  const setContext = (type: string, payload: any) => {
    dispatch({ type, payload });
  };

  const { translations } = store;

  useEffect(() => {
    if (customLanguage) {
      setContext('translations', customLanguage);
    } else if (language) {
      setContext('translations', getKnownLanguage(language));
    }
  }, []);

  useEffect(() => {
    if (customLanguage) {
      setContext('translations', customLanguage);
    } else if (language) {
      setContext('translations', getKnownLanguage(language));
    }
  }, [customLanguage, language]);

  return translations ? props.children : null;
};

export default LanguageLayer;
import { useEffect, useState } from 'react';

const RootLayoutLayer = (props: { children: any }) => {
  const [isMounted, setIsMounted] = useState(false);

  useEffect(() => {
    const rootEl: any = document.querySelector('.Kalend__Calendar__root');

    if (rootEl) {
      setIsMounted(true);
    }
  }, [document.querySelector('.Kalend__Calendar__root')]);

  return isMounted ? props.children : null;
};

export default RootLayoutLayer;
import { CALENDAR_VIEW } from './common/enums';
import {
  CalendarEvent,
  Colors,
  NewEventClickData,
  OnEventClickFunc,
  OnEventDragFinishFunc,
  OnNewEventClickFunc,
  OnPageChangeFunc,
  OnSelectViewFunc,
  PageChangeData,
  ShowMoreMonthFunc,
  Style,
} from './common/interface';
import { useEffect } from 'react';
import { validateProps, validateStyle } from './utils/validator';
import Calendar from './Calendar';
import ConfigLayer from './layers/ConfigLayer';
import DimensionsLayoutLayer from './layers/DimensionsLayoutLayer';
import LanguageLayer from './layers/LanguageLayer';
import RootLayoutLayer from './layers/RootLayoutLayer';
import StoreProvider from './context/store';

export type { CALENDAR_VIEW };
export const CalendarView = CALENDAR_VIEW;
export type { CalendarEvent };
export type OnEventClickData = CalendarEvent;
export type OnNewEventClickData = NewEventClickData;
export type OnPageChangeData = PageChangeData;
export type OnSelectViewData = CALENDAR_VIEW;
export type ShowMoreMonthData = CalendarEvent[];
export type OnEventDragFinish = OnEventDragFinishFunc;

export interface KalendProps {
  initialDate?: string;
  initialView?: CALENDAR_VIEW;
  selectedView?: CALENDAR_VIEW;
  disabledViews?: CALENDAR_VIEW[];
  events?: any;
  isDark?: boolean;
  hourHeight?: number;
  onNewEventClick?: OnNewEventClickFunc;
  onEventClick?: OnEventClickFunc;
  onSelectView?: OnSelectViewFunc;
  showMoreMonth?: ShowMoreMonthFunc;
  onPageChange?: OnPageChangeFunc;
  onEventDragFinish?: OnEventDragFinishFunc;
  draggingDisabledConditions?: { [key: string]: boolean | string | number };
  isNewEventOpen?: boolean;
  onStateChange?: any;
  disableMobileDropdown?: boolean;
  timezone?: string;
  weekDayStart?: string;
  timeFormat?: string;
  calendarIDsHidden?: string[];
  children?: any;
  language?: string;
  customLanguage?: any;
  eventLayouts?: any;
  kalendRef?: any;
  style?: Style;
  focusHour?: number;
  showTimeLine?: boolean;
  showWeekNumbers?: boolean;
  colors?: Colors;
  autoScroll?: boolean;
}

// use any as JSX was causing errors for some cases
const Kalend = (props: KalendProps): any => {
  // basic validation
  useEffect(() => {
    validateProps(props);
    validateStyle();
  }, []);

  return (
    <div className={'Kalend__Calendar__root Kalend__main'}>
      <StoreProvider {...props}>
        <LanguageLayer
          language={props.language || 'en'}
          customLanguage={props.customLanguage}
        >
          <RootLayoutLayer>
            <ConfigLayer {...props}>
              <DimensionsLayoutLayer>
                <Calendar
                  kalendRef={props.kalendRef}
                  events={props.events}
                  eventLayouts={props.eventLayouts}
                  selectedView={props.selectedView}
                  initialDate={props.initialDate}
                />
              </DimensionsLayoutLayer>
            </ConfigLayer>
          </RootLayoutLayer>
        </LanguageLayer>
      </StoreProvider>
    </div>
  );
};

export default Kalend;
