import React, { useContext, useState } from 'react'
import { Auths } from '../context/auths';
import MyButton from '../UI/Button/MyButton';
import { Navigate } from 'react-router-dom';
import 'react-calendar/dist/Calendar.css';
import Calendar from 'react-calendar';
import MyInput from '../UI/Input/MyInput';
import MyInput2 from '../UI/Input/MyInput2';

const Calendars = () => {

    const { isAuth, setIsAuth } = useContext(Auths);


    var options = {
        year: 'numeric',
        month: 'numeric',
        day: 'numeric',
        timezone: 'UTC'
    };

    const [value, onChange] = useState(new Date());

    const [dela, setDela] = useState([
        { id: 1, nam: 'Событие', title: "Охота", main: "Поймать рыбу" },
        { id: 2, nam: 'Задача', title: "Ужин", main: "Поймать рыбу" },
        { id: 3, nam: 'Событие', title: "Обед", main: "Поймать рыбу" },
    ]);

    const [nam, setNam] = useState('');
    const [title, setTitle] = useState('');
    const [main, setMain] = useState('');


    if (isAuth === false) {
        return <Navigate to="/login" />
    }



    console.log(value);
    return (
        <div>
            <MyButton
                className="calendar__btn"
                style={{ margin: '0' }}
                onClick={() => {
                    setIsAuth(false);
                    localStorage.clear();
                }}>
                Выход
            </MyButton>

            <Calendar onChange={onChange} value={value} defaultValue={onChange} />



            <input type="text"
                value={value}
            />

            <div style={{ display: "flex", flexDirection: "column", alignItems: "end", width: '274px' }}>
                <MyInput2
                    title={"Собитие"}
                    value={nam}
                    onChange={e => { setNam(e.target.value); console.log(nam); }}
                />
                <MyInput2
                    title={"Название"}
                />
                <MyInput2
                    title={"Описание"}
                />
            </div>

            <div>
                <MyButton>Добавить</MyButton>
            </div>

            <div>
                {dela.map((dela) =>
                    <div style={{ margin: "30px 0" }} key={dela.id}>
                        {dela.nam}:
                        <div style={{ display: "flex", flexDirection: "column" }}>
                            <strong>{
                                dela.title}
                            </strong>
                            {dela.main}
                        </div>
                    </div>
                )}
            </div>

        </div>
    )
}

export default Calendars
