import React, { useContext, useState } from 'react'
import { Auths } from '../context/auths';
import MyButton from '../UI/Button/MyButton';
import { Navigate } from 'react-router-dom';
import 'react-calendar/dist/Calendar.css';
import Calendar from 'react-calendar';
import MyInput from '../UI/Input/MyInput';

const Calendars = () => {
    const { isAuth, setIsAuth } = useContext(Auths);


    var options = {
        year: 'numeric',
        month: 'numeric',
        day: 'numeric',
        timezone: 'UTC'
    };

    const [value, onChange] = useState(new Date());

    const [dela, setDela] = useState([
        { nam: 'Событие', title: "Охота", main: "Поймать рыбу" },
        { nam: 'Задача', title: "Ужин", main: "Поймать рыбу" },
        { nam: 'Событие', title: "Обед", main: "Поймать рыбу" },
    ]);

    if (isAuth === false) {
        return <Navigate to="/login" />
    }



    console.log(value);
    return (
        <div>
            <MyButton
                className="calendar__btn"
                style={{ margin: '0' }}
                onClick={() => {
                    setIsAuth(false);
                    localStorage.clear();
                }}>
                Выход
            </MyButton>

            <Calendar key={Date.now()} onChange={onChange} value={value} />



            <input type="text"
                value={value}
            />

            <div style={{ display: "flex", flexDirection: "column", alignItems: "start" }}>
                <MyInput className="calendar__input" title="Событие" />
                <MyInput className="calendar__input" title="Название" />
                <MyInput className="calendar__input" title="Описание" />
            </div>

            <div>
                {dela.map((dela) =>
                    <div style={{ margin: "30px 0" }}>
                        {dela.nam}:
                        <div style={{ display: "flex", flexDirection: "column" }}>
                            <strong>{
                                dela.title}
                            </strong>
                            {dela.main}
                        </div>
                    </div>
                )}
            </div>

        </div>
    )
}

export default Calendars
