import React, { useContext, useState } from 'react'
import { Auths } from '../context/auths';
import MyButton from '../UI/Button/MyButton';
import { Navigate } from 'react-router-dom';
import 'react-calendar/dist/Calendar.css';
import Calendar from 'react-calendar';
import NewNam from '../calendar/NewNam';

const Calendars = () => {

    const { isAuth, setIsAuth } = useContext(Auths);
    const [value, onChange] = useState(new Date());

    const [dela, setDela] = useState([
        { id: 1, nam: 'Событие', title: "Охота", main: "Поймать рыбу" },
        { id: 2, nam: 'Задача', title: "Ужин", main: "Поймать рыбу" },
        { id: 3, nam: 'Событие', title: "Обед", main: "Поймать рыбу" },
    ]);

    const createDela = (newDela) => {
        setDela([...dela, newDela])
    }


    if (isAuth === false) {
        return <Navigate to="/login" />
    }



    console.log(value);
    return (
        <div>
            <MyButton
                className="calendar__btn"
                style={{ margin: '0' }}
                onClick={() => {
                    setIsAuth(false);
                    localStorage.clear();
                }}>
                Выход
            </MyButton>

            <Calendar onChange={onChange} defaultValue={value} />



            <input type="text"
                defaultValue={value}
            />

            <NewNam
                create={createDela}
            />

            <div className='calendar-dela'>
                {dela.map((dela) =>
                    <div className='dela__block' key={dela.id}>
                        <div className='dela__nam'>
                            {dela.nam}:
                        </div>

                        <div className="dela__content">
                            <strong>
                                {dela.title}
                            </strong>
                            {dela.main}
                        </div>
                    </div>
                )}
            </div>

        </div>
    )
}

export default Calendars
